import "./styles.css";
import React, { useState } from "react";
import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';

export default function App() {
  const modelRef = React.useRef();
  const [annots, setAnnots] = useState([]);

  const handleClick = (event) => {
    const { clientX, clientY } = event;

    if (modelRef.current) {
      let hit = modelRef.current.positionAndNormalFromPoint(clientX, clientY);
      if (hit) {
        setAnnots((annots) => {
          return [...annots, hit];
        });
      }
    }
  };

  function valuetext(value) {
    return `${value}`;
  }

  const [inputValue, setInputValue] = useState('');
  const [inputBatch, setBatch] = useState(4);
  const [inputGuide, setGuide] = useState(15);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };
  // Define a function to run when the button is clicked
  const handleSubmit = () => {
    // Execute your function with the input value
    runFunction(inputValue);
  };

  // Replace this function with the one you want to run
  const runFunction = (input) => {
    // Perform the desired operation using the input
    alert(`Function executed with input: ${input} ${inputGuide} ${inputBatch}`);
  };

  return (
    <div>
      <div style={{ display: "block", color: "black" }}>
        <div style={{display: "flex"}}>
          <form onSubmit={handleSubmit}  style={{display: "flex"}}>
            <label>
                Enter some input:
                <input
                  type="text"
                  value={inputValue}
                  onChange={handleInputChange}
                  placeholder="Enter some input"
                />
              </label>
              <button type="submit">Generate model</button>
            </form>
        </div>
        <div style={{display: "flex"}}>
          <div style={{ paddingRight:"25px"}}>
            <h4>
              Batch Size
            </h4>
            <Box sx={{ width: 300 }}>
              <Slider
                aria-label="Batch Size"
                defaultValue={4}
                value={inputBatch}
                getAriaValueText={valuetext}
                valueLabelDisplay="auto"
                step={1}
                marks
                min={1}
                max={10}
              />
            </Box>
          </div>
          <div>
            <h4>
              Guidance Scale
            </h4>
            <Box sx={{ width: 300 }}>
              <Slider
                aria-label="Guidance Scale"
                defaultValue={15}
                value={inputGuide}
                getAriaValueText={valuetext}
                valueLabelDisplay="auto"
                min={1}
                max={100}
              />
            </Box>
          </div>
        </div>

        <div style={{ display: "flex"}}>
        <form>
          <label>
            Output Name:
            <input type="text" name="outputname" />
          </label>
        </form>
        </div>

      </div>

      <div>
        <model-viewer
          // className="model-viewer"
          src="./M08.glb"
          alt="A rock"
          exposure="0.008"
          camera-controls
          ar
          ar-modes="webxr"
          onClick={handleClick}
          ref={(ref) => {
            modelRef.current = ref;
          }}
        >
        </model-viewer>  
      </div>

    </div>
  );
}
